// SPDX-License-Identifier: GPL-3.0

pragma solidity 0.8.10;

library safeMath {
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        assert(b <= a);
        return a - b;
    }

    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        assert(c >= a);
        return c;
    }
}

interface IERC20 {
    // è·å–åˆçº¦åœ°å€
    function getAddress() external view returns (address);
    // è·å–ä»£å¸å‘è¡Œæ€»é‡
    function totalSupply() external view returns (uint256);
    // æ ¹æ®åœ°å€è·å–ä»£å¸çš„ä½™é¢
    function balanceOf(address account) external view returns (uint256);
    // ä»£ç†å¯è½¬ç§»çš„ä»£å¸æ•°é‡
    function allowance(address owner, address supender) external view returns (uint256);

    // è½¬è´¦
    function transfer(address recipient, uint256 amount) external returns (bool);
    // è®¾ç½®ä»£ç†èƒ½è½¬è´¦çš„é‡‘é¢
    function approve(address owner, address spender, uint256 amount) external returns (bool);
    // è½¬è´¦
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

// ä»£å¸å®ç°
contract ERC20Basic is IERC20 {
    string public constant name = "ERC-xinChain"; // ä»£å¸åç§°
    string public constant symbol = "ERC-xin"; // ä»£å¸ç®€ç§°
    uint8 public constant decimals = 18;

    mapping(address => uint256) balances; // åœ°å€å¯¹åº”çš„ä½™é¢æ•°é‡
    mapping(address => mapping(address => uint256)) allowedBalence; // ä»£ç†å•†èƒ½å¤„ç†çš„ä»£å¸æ•°é‡

    uint256 totalSupply_ = 10 ether; // å‘è¡Œæ•°é‡ï¼ŒetheræŒ‡çš„æ˜¯å•ä½ï¼Œç±»ä¼¼å¨ï¼Œä¹Ÿå¯ä»¥ä½¿ç”¨8ä¸ª0

    using safeMath for uint256;

    constructor () {
        balances[msg.sender] = totalSupply_; // å°†ä»£å¸åˆ†å‘ç»™åˆ›å»ºè€…
    } 

     // è·å–åˆçº¦åœ°å€
    function getAddress() external view returns (address){
        return address(this); // å½“å‰åˆçº¦çš„åœ°å€
    }
    // è·å–ä»£å¸å‘è¡Œæ€»é‡
    function totalSupply() external view returns (uint256){
        return totalSupply_;
    }

    // æ ¹æ®åœ°å€è·å–ä»£å¸çš„ä½™é¢
    function balanceOf(address tokenOwner) public override view returns (uint256){
        return balances[tokenOwner]; // æ ¹æ®åœ°å€è·å–ä½™é¢
    }

    // è½¬è´¦
    function transfer(address receiver, uint256 amount) public override returns (bool){
        require(amount <= balances[msg.sender]);
        balances[msg.sender] = balances[msg.sender].sub(amount);
        balances[receiver] = balances[receiver].add(amount);
        emit Transfer(msg.sender, receiver, amount);
        return true;
    }

    // è®¾ç½®ä»£ç†èƒ½è½¬è´¦çš„é‡‘é¢
    function approve(address owner, address delegate, uint256 amount) external returns (bool){
        allowedBalence[owner][delegate] = amount;
        emit Approval(owner, delegate, amount);
        return true;
    }

    // ä»£ç†å¯è½¬ç§»çš„ä»£å¸æ•°é‡
    function allowance(address owner, address delegate) external view returns (uint256){
        return allowedBalence[owner][delegate];
    }

    // è½¬è´¦
    function transferFrom(address owner, address buyer, uint256 amount) external returns (bool){
        require(amount <= balances[owner]);
        require(amount <= allowedBalence[owner][msg.sender]);

        balances[owner] = balances[owner].sub(amount);
        allowedBalence[owner][buyer] = allowedBalence[owner][buyer].sub(amount);
        balances[buyer] = balances[buyer].add(amount);
        emit Transfer(owner, buyer, amount);
        return true;
    }
}


contract DEX {
    event Bought(uint256 amount);
    event Sold(uint256 amount);

    IERC20 public token;

    constructor () {
        token = new ERC20Basic();
    }

    // ä¹°å…¥
    function buy() payable public {
        uint256 amountTobuy = msg.value; //ä¼ å…¥ä»¥å¤ªåŠ
        uint256 dexBalance = token.balanceOf(address(this)); //æ­¤åˆçº¦ä¸­è‡ªå·±åˆ›å»ºä»£å¸çš„æ•°é‡
        require(amountTobuy > 0 , "You need to send some Ethoer"); // amountTobuy å¿…é¡»ä¼ å…¥ä»¥å¤ªï¼Œä½¿ç”¨ä»¥å¤ªè´­ä¹°æ­¤ä»£å¸
        require(amountTobuy <= dexBalance, "Not enough tokens in the reserve"); // åˆçº¦ä¸­ä»£å¸çš„æ•°é‡è¦å¤§äºè¦è´­ä¹°çš„é‡
        token.transfer(msg.sender, amountTobuy);
        emit Bought(amountTobuy);
    }

    function sell(uint256 amount) public {
        require(amount > 0, "You need to sell at least some tokens."); // å–å‡ºæ•°é‡è¦å¤§äº0
        uint256 allowance = token.allowance(msg.sender, address(this));
        require(allowance >= amount, "check the token allowance");
        token.transferFrom(msg.sender, address(this), amount);

        emit Sold(amount);
    }

    function getDexBalance() public view returns(uint256) {
        return token.balanceOf(address(this));
    }

    function getOwnerBalance() public view returns(uint256) {
        return token.balanceOf(msg.sender);
    }

    function getAddress() public view returns (address) {
        return address(this);
    }

    function getTokenAddress() public view returns (address) {
        return token.getAddress();
    }

    function getTotalSupply() public view returns (uint256) {
        return token.totalSupply();
    }

    function getSenderAddress() public view returns (address) {
        return address(msg.sender);
    }

    function getAllowance() public view returns (uint256) {
        uint256 allowance = token.allowance(msg.sender, address(this));
        return allowance;
    }

    // æˆæƒå½“å‰åˆçº¦è½¬ç§»ä»£å¸æ•°é‡
    function approve(uint256 amount) public returns(bool) {
        bool isApprove = token.approve(msg.sender, address(this), amount);
        return isApprove;
    }
}
