    //å‡ºä»·
    function bid() external payable {
        //æ£€æŸ¥æ‹å–æ˜¯å¦ç»“æŸ
        if(block.timestamp > auctionEndTime || ended){
           revert AuctionAlredayEnded();
        }

        //æ£€æŸ¥å‡ºä»·
        if(msg.value <= highestBid){
            revert BidNotHighEnough(highestBid);
